import java.util.Arrays;import java.util.HashSet;public class TreeNode {    public static void main(String[] args) {    }    public  int pivotIndex(int[] nums) {        int total = Arrays.stream(nums).sum();        int sum = 0;        for (int i = 0; i < nums.length; ++i) {            if (2 * sum + nums[i] == total) {                return i;            }            sum += nums[i];        }        return -1;    }        int val;        TreeNode left;        TreeNode right;        TreeNode() {        }        TreeNode(int val) {            this.val = val;        }        TreeNode(int val, TreeNode left, TreeNode right) {            this.val = val;            this.left = left;            this.right = right;        }}