package sort;import java.util.Arrays;/** * @author dongjunzhi * @Title: BubbleSort * @ProjectName LeetCode * @Description: å†’æ³¡ * @create 2024/7/28 10:15 */public class BubbleSort {    public static void main(String[] args) {        int[] nums =new int[]{2,3,1,7,5,8,9};        for (int n = nums.length-1; n >= 0; n--) {            for (int i = 0; i < n; i++) {                if (nums[i]>nums[i+1]){                    int tmp= nums[i];                    nums[i]=nums[i+1];                    nums[i+1]=tmp;                }            }        }        System.out.println(Arrays.toString(nums));    }}