package sort;import java.util.Arrays;/** * @author dongjunzhi * @Title: InsertionSort * @ProjectName LeetCode * @Description: 插入排序 * @create 2024/7/28 10:19 */public class InsertionSort {    public static void main(String[] args) {        int[] nums =new int[]{2,3,1,7,5,8,9};        for (int i = 1; i < nums.length; i++) {            int base = nums[i], j = i - 1;            while (j>=0&&nums[j]<base){                 nums[j+1]=nums[j];                 j--;            }            nums[j + 1] = base;        }        System.out.println(Arrays.toString(nums));    }}